import 'package:flutter/material.dart';

class SettingsProvider with ChangeNotifier {
  ThemeMode themeMode = ThemeMode.light;
  String languageCode = 'en';
  bool get isDark => themeMode == ThemeMode.dark;
  String get splashLogoImageName => isDark ? 'splash_logo_dark' : 'splash_logo';
  String get backgroundImageName => isDark ? 'dark_bg' : 'default_bg';
  String get headSebhaImageName =>
      isDark ? 'head_sebha_dark' : 'head_sebha_logo';
  String get bodySebhaImageName =>
      isDark ? 'body_sebha_dark' : 'body_sebha_logo';
  static const String themeKey = 'theme_mode';
  static const String languageKey = 'language_code';
  SettingsProvider() {
    loadSettings();
  }

  void changeTheme(ThemeMode selectedTheme) async {
    themeMode = selectedTheme;
    notifyListeners();
    SharedPreferences prefs = await SharedPreferences.getInstance();
    await prefs.setString(
        themeKey, selectedTheme == ThemeMode.dark ? 'dark' : 'light');
  }

  void changeLanguage(String selectedLanguage) async {
    if (selectedLanguage == languageCode) return;
    languageCode = selectedLanguage;
    notifyListeners();
    SharedPreferences prefs = await SharedPreferences.getInstance();
    await prefs.setString(languageKey, selectedLanguage);
  }

  Future<void> loadSettings() async {
    SharedPreferences prefs = await SharedPreferences.getInstance();
    String? savedTheme = prefs.getString(themeKey);
    themeMode = savedTheme == 'dark' ? ThemeMode.dark : ThemeMode.light;

    String? savedLanguage = prefs.getString(languageKey);
    languageCode = savedLanguage ?? 'en';

    notifyListeners();
  }
}
